mapper.select:
select
id,
sender_id sender,
receiver_id receiver,
lm_date date,
lm_title title,
lm_content content
from tas.leaving_message

    is_deleted = '未删除'

        and ( sender_id = #{sender}

            or receiver_id = #{receiver}

        )


        and lm_title = #{title}


        and lm_date BETWEEN #{startTime} and #{endTime}

select distinct lm_title allTitle
from tas.leaving_message

    is_deleted = '未删除'

        and sender_id = #{sender}


        or receiver_id = #{receiver}
