mapper.update:
update tas.leaving_message set is_deleted = '已删除'
id in
#{item}

mapper.select:
select
id,sender,receiver,date,title,content
from tas.leaving_message

    is_deleted = '未删除'

        and ( sender = #{sender}

            or receiver = #{receiver}

        )


        and title = #{title}


        and date BETWEEN #{startTime} and #{endTime}

select distinct title allTitle
from tas.leaving_message

    is_deleted = '未删除'

        and sender = #{sender}


        or receiver = #{receiver}
